import logo from './logo.svg';
import './App.css';
import { useState } from 'react';


function App() {
  const [listOne , setListOne] = useState([
    1, 2,3,4,5
  ]);
  const [listTwo, setListTwo] = useState([6, 7, 8,9, 10])
  const [itemDrag, setItemDrag] = useState(null);
  const onDrag = (e) =>{
    e.preventDefault();

  }
  const onDrop = (e)=>{
    e.preventDefault();
    console.log(e.target.id)
  }
  return (
    <div className="App">
      <h1>
        Drag and drop
      </h1>
      <div className='list-flex' >
        <div onDrop={onDrop} onDragEnd={onDrop} onDrag={onDrag} className='list-one '>
        {listOne.map((some, index)=>{
          return (
            <a id={some} draggable={true}

            >{some}</a>
          )
        })}
        </div>
        <div className='list-two'>
          {listTwo.map((some, index)=>{
            return(
              <a id={some} draggable={true} onDrag={onDrag}

              >{some}</a>
            )
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
