import logo from './logo.svg';
import './App.css';
import { useState } from 'react';


function App() {
  const [listOne , setListOne] = useState([
    1, 2,3,4,5
  ]);
  const [listTwo, setListTwo] = useState([6, 7, 8,9, 10])
  const [itemDrag, setItemDrag] = useState(null);
  const [itemDrop, setItemDrop] = useState("")
  const onDrag = (e) =>{
    e.preventDefault();
    setItemDrag(e.target.id)
  }
  const onDrop = (e)=>{
    e.preventDefault();
    console.log(e.target.id)
    //setItemDrop(e.target.id)
    if(listOne.includes(Number(itemDrag)) && e.target.id === 'list-two'){
      //setListOne(listOne.filter(prev=>{prev !== Number(itemDrag)}));
      setListTwo([...listTwo, Number(itemDrag)])
    }
    if(listTwo.includes(Number(itemDrag)) && e.target.id === 'list-one'){
      //setListTwo(listTwo.filter(prev=>{prev !== Number(itemDrag)} ))
      setListOne([...listOne, Number(itemDrag)])

    }
  }

  return (
    <div className="App">
      <h1>
        Drag and drop
      </h1>
      <div className='list-flex' >
        <div onDrop={onDrop} onDragEnd={onDrop} id={"list-one"}  className='list-one'>
        {listOne.map((some, index)=>{
          return (
            <a id={some} draggable={true} onDrag={onDrag}

            >{some}</a>
          )
        })}
        </div>
        <div className='list-two' onDrop={onDrop} id={"list-two"}>
          {listTwo.map((some, index)=>{
            return(
              <a id={some} draggable={true} onDragStart={onDrag}

              >{some}</a>
            )
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
